import './login.css';
import 'antd/dist/antd.css';
import {Row,Col,Button,Form,Input,Icon} from 'antd';
import {regExpConfig} from '../../config/regular.config.js'
import QueueAnim from 'rc-queue-anim';

const FormItem = Form.Item;

export default class Login extends React.Component{

    constructor(props){
        super(props);
       this.state={
           show : false
       }
    }

    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
          if (!err) {
            console.log('Received values of form: ', values);
          }
        });
      }

     onChange() {
        this.setState({
            show : !this.state.show
        })
    }

    componentDidMount() {
        // To disabled submit button at the beginning.
        this.props.form.validateFields();
      }
    render(){

          const {
      getFieldDecorator, getFieldsError, getFieldError, isFieldTouched,
    } = this.props.form;

        return(
            <div className="LoginContainer">
                {/* <Button type="primary" onClick={this.onChange.bind(this)}>show</Button>
                <QueueAnim className="demo-content" type={['left','right']} 
                ease={['easeOutQuart', 'easeInOutQuart']}
                >
                </QueueAnim> */}
                <div className='start-of-content'>
                </div>
                <div className='content'>
                    <Row>
                      <Col className="" span={8}>

                     </Col>
                   </Row>
                </div>

            </div>
        )
    }
}
