import React from 'react';
import './login.css';
import 'antd/dist/antd.css';
import {Row,Col,Button,Form,Input,Icon} from 'antd';
import {regExpConfig} from '../../config/regular.config.js'
import QueueAnim from 'rc-queue-anim';

const FormItem = Form.Item;

export default class Login extends React.Component{

    constructor(props){
        super(props);
       this.state={
           show : false
       }
    }

     onChange() {
        this.setState({
            show : !this.state.show
        })
    }

    render(){

        const { getFieldDecorator } = this.props.form;

        return(
            <div className="LoginContainer">
                {/* <Button type="primary" onClick={this.onChange.bind(this)}>show</Button>
                <QueueAnim className="demo-content" type={['left','right']} 
                ease={['easeOutQuart', 'easeInOutQuart']}
                >
                </QueueAnim> */}
                <div className='start-of-content'>
                </div>
                <div className='content'>
                    <Row>
                      <Col className="" span={8}>
                      <FormItem hasFeedback>
                                {getFieldDecorator('username', {
                                  rules: [
                                    { pattern: regExpConfig.policeNo, message: '账号4-10位数字或字母组成' },
                                  ],
                                })(<Input addonBefore={<Icon type="user" />} placeholder="usernmae" type="text" />)}
                      </FormItem>
                     <FormItem hasFeedback>
                                {getFieldDecorator('password', {
                                  rules: [
                                    { pattern: regExpConfig.pwd, message: '密码由6-16位数字或者字母组成' },
                                  ],
                                })(<Input addonBefore={<Icon type="user" />} placeholder="password" type="text" />)}
                      </FormItem>
                     </Col>
                   </Row>
                </div>

            </div>
        )
    }
}
