import React from 'react';
import './login.css';
import 'antd/dist/antd.css';
import {Row,Col,Button,Form,Input,Icon} from 'antd';
import {regExpConfig} from '../../config/regular.config.js'
import QueueAnim from 'rc-queue-anim';

const FormItem = Form.Item;

export default class Login extends React.Component{

    constructor(props){
        super(props);
       this.state={
           show : true
       }

    }

     onChange() {
        this.setState({
            show : !this.state.show
        })
    }


    loginCol(){

      const { getFieldDecorator } = this.props.form;
      return(
        <Col className="login-content-a" span={6} offset={9} key ="login">

        Username

      <FormItem hasFeedback >
                {getFieldDecorator('username', {
                  rules: [
                    { pattern: regExpConfig.policeNo, message: '账号4-10位数字或字母组成' },
                  ],
                })(<Input addonBefore={<Icon type="user" />}  type="text" />)}
      </FormItem>



      Password
     <FormItem hasFeedback key = 'b'>
                {getFieldDecorator('password', {
                  rules: [
                    { pattern: regExpConfig.pwd, message: '密码由6-16位数字或者字母组成' },
                  ],
                })(<Input addonBefore={<Icon type="lock" />} type="text" />)}
      </FormItem>
      <Button size="large" className="login-btn">sign in</Button>

           <Button  className="login-btn" onClick={this.onChange.bind(this)}>Create an account</Button> 

     </Col>
      )
    }

    signinCol(){

      const { getFieldDecorator } = this.props.form;
      return(
        <Col className="login-content-a" span={6} offset={9} key ="sigin">

        Username

      <FormItem hasFeedback >
                {getFieldDecorator('username', {
                  rules: [
                    { pattern: regExpConfig.policeNo, message: '账号4-10位数字或字母组成' },
                  ],
                })(<Input addonBefore={<Icon type="user" />}  type="text" />)}
      </FormItem>




      Password
     <FormItem hasFeedback >
                {getFieldDecorator('password', {
                  rules: [
                    { pattern: regExpConfig.pwd, message: '密码由6-16位数字或者字母组成' },
                  ],
                })(<Input addonBefore={<Icon type="lock" />} type="text" />)}
        </FormItem>
       <Button size="large" className="login-btn">sign in</Button>

       <Button type="success" className="login-btn" onClick={this.onChange.bind(this)}>aleady have an account</Button> 

     </Col>
      )
    }

    render(){



        return(
            <div className="LoginContainer">


                <div className='content'>
                    <Row>
                     <QueueAnim className="demo-content" type={['left','right']} 
                      ease={['easeOutQuart', 'easeInOutQuart']}
                      leaveReverse
                   >
                    {
                        this.state.show?[  
                          this.loginCol()
                         ] : this.signinCol()
                    }
                     </QueueAnim>

                   </Row>
                </div>

            </div>
        )
    }
}
