import React, { Component } from 'react';

import logo from './logo.svg';
import './App.css';


const numbers = [1,2,3,4,5];  
const post = [
  {id : 1,title : "hello world", content : "welcome to learning react"},
  {id : 1,title : "Installation", content : "you can install React from npm"}
]
var listItem = numbers.map((numbers)=>
  <li>{numbers}</li>
)

  function Blog(props){
    const posts = props.post;
    const sidebar = (
      <ul>
      {posts.map((post)=>
      <li key={post.id}>
        {post.title}
      </li>
      )}
      </ul>
    )

    const content = posts.map((post)=>
    <div>
      <h3>{post.title}</h3>
      <p>{post.content}</p>
    </div>
    )

    return (
      <div>
        {sidebar}
        <hr/>
        {content}
      </div>
    )
  }

  function WarningBanner(props){
    if(!props.wran){
      return null;
    }

    return(
      <div className="warning">
        这他娘的是警告!
        <Button type='primary'/>
      </div>
    );
  }

class App extends Component {

  constructor(props){
    super(props);


    this.handleclick=this.handleclick.bind(this);

    this.state={
      show_warning:true
    }
  }

   handleclick() {
     console.log(this.state.show_warning);
    this.setState(prevState=>({
      show_warning:!prevState.show_warning
    }))
  }

  render() {


    return (
      <div>
          <WarningBanner wran={this.state.show_warning}/>

          <button onClick={this.handleclick}>
              {this.state.show_warning?"隐藏":"显示"}
          </button>
          <ul>{listItem}</ul>
          <Blog post={post}/>
      </div>

    );
  }
}

export default App;
