import React from 'react';
import {Menu} from 'antd';
import 'antd/dist/antd.css';
import {sidebar} from '../../menu.js';

const SubMenu = Menu.SubMenu;



export class SideBar extends React.Component{

    constructor(props){
        super(props);

    }

    getNodes(sidebar){
        return(
              sidebar.map((side)=>{
                if(side.child==null){
                    console.log(side.value)
                  return(
                      <Menu.Item key = {side.key}>{side.title}</Menu.Item>
                  )
                }else{
                    let childmenu=side.child;
                    return(
                      <SubMenu key = {side.key} title={side.title} >
                           {childmenu.map((child)=>{
                              return(
                                  <Menu.Item key={child.key}>{child.title}</Menu.Item>
                              ) 
                           })}
                      </SubMenu>
                    )
                }
              })

        )
    }

    render(){
        return(
            <div className={sidebar}>
               <Menu
               style={{width:'200px'}}

               >
                 {
                    this.getNodes(sidebar)
                 }  
               </Menu>
            </div>
        )
    }
}
